import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, ArcElement, Tooltip, Legend } from 'chart.js';

// Register chart components
ChartJS.register(CategoryScale, LinearScale, BarElement, ArcElement, Tooltip, Legend);

const App = () => {
  const [month, setMonth] = useState('March');
  const [transactions, setTransactions] = useState([]);
  const [statistics, setStatistics] = useState({});
  const [barChart, setBarChart] = useState({});
  const [pieChart, setPieChart] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch transactions
        const transRes = await axios.get(`http://localhost:5000/api/transactions?month=${month}`);
        setTransactions(transRes.data);

        // Fetch statistics
        const statsRes = await axios.get(`http://localhost:5000/api/statistics?month=${month}`);
        setStatistics(statsRes.data);

        // Fetch bar chart data
        const barRes = await axios.get(`http://localhost:5000/api/bar-chart?month=${month}`);
        setBarChart(barRes.data);

        // Fetch pie chart data
        const pieRes = await axios.get(`http://localhost:5000/api/pie-chart?month=${month}`);
        setPieChart(pieRes.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [month]);

  // Prepare data for the bar chart
  const barChartData = {
    labels: Object.keys(barChart), // Price ranges
    datasets: [
      {
        label: 'Number of Items',
        data: Object.values(barChart),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  // Prepare data for the pie chart
  const pieChartData = {
    labels: Object.keys(pieChart), // Categories
    datasets: [
      {
        data: Object.values(pieChart),
        backgroundColor: [
          '#FF6384',
          '#36A2EB',
          '#FFCE56',
          '#4BC0C0',
          '#9966FF',
          '#FF9F40',
        ],
      },
    ],
  };

  return (
    <div>
      <div className='w-full h-40 text-center py-10 bg-red-500'>
      <h1 className='font-semibold text-3xl'>Transactions Dashboard</h1>
      </div>

      <div className='w-full h-full  text-right px-2 py-2 '>
      <label className='text-xl font-medium'>Select Month: </label>
      <select className='border rounded-lg px-1 py-1 outline-none' value={month} onChange={(e) => setMonth(e.target.value)}>
        {['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'].map((mon) => (
          <option key={mon} value={mon}>
            {mon}
          </option>
        ))}
      </select>
      </div>

      {/* Transactions Table */}
      <div className='w-full h-full bg-yell'>
      <h2>Transactions Table</h2>
      <table border="1" cellPadding="10">
        <thead>
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Price</th>
            <th>Date of Sale</th>
            <th>Sold</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((transaction) => (
            <tr key={transaction.id}>
              <td>{transaction.title}</td>
              <td>{transaction.description}</td>
              <td>${transaction.price}</td>
              <td>{new Date(transaction.dateOfSale).toLocaleDateString()}</td>
              <td>{transaction.sold ? 'Yes' : 'No'}</td>
            </tr>
          ))}
        </tbody>
      </table>
      </div>

      {/* Display statistics */}
      <h2>Statistics</h2>
      <div>
        <p>Total Sales: ${statistics.totalSales}</p>
        <p>Total Sold Items: {statistics.totalSold}</p>
        <p>Total Not Sold Items: {statistics.totalNotSold}</p>
      </div>

      {/* Bar Chart */}
      <h2>Bar Chart</h2>
      <Bar data={barChartData} />

      {/* Pie Chart */}
      <h2>Pie Chart</h2>
      <Pie data={pieChartData} />
    </div>
  );
};

export default App;
