import React, { useEffect, useState } from 'react';
import axios from 'axios';

const App = () => {
    const [month, setMonth] = useState('March');
    const [transactions, setTransactions] = useState([]);
    const [statistics, setStatistics] = useState({});
    const [barChart, setBarChart] = useState({});
    const [pieChart, setPieChart] = useState({});

    useEffect(() => {
        const fetchData = async () => {
            const transactionsRes = await axios.get(`http://localhost:5000/api/transactions?month=${month}`);
            const statsRes = await axios.get(`http://localhost:5000/api/statistics?month=${month}`);
            const barChartRes = await axios.get(`http://localhost:5000/api/bar-chart?month=${month}`);
            const pieChartRes = await axios.get(`http://localhost:5000/api/pie-chart?month=${month}`);

            setTransactions(transactionsRes.data);
            setStatistics(statsRes.data);
            setBarChart(barChartRes.data);
            setPieChart(pieChartRes.data);
        };

        fetchData();
    }, [month]);

    return (
        <div>
            <h1>Transactions Dashboard</h1>
            <label>Select Month: </label>
            <select value={month} onChange={(e) => setMonth(e.target.value)}>
                {['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'].map(mon => (
                    <option key={mon} value={mon}>{mon}</option>
                ))}
            </select>

            <h2>Statistics</h2>
            <p>Total Sales: ${statistics.totalSales}</p>
            <p>Total Sold Items: {statistics.totalSold}</p>
            <p>Total Not Sold Items: {statistics.totalNotSold}</p>

            <h2>Transactions</h2>
            <ul>
                {transactions.map(tx => (
                    <li key={tx.id}>{tx.title} - ${tx.price}</li>
                ))}
            </ul>

            <h2>Bar Chart Data</h2>
            <pre>{JSON.stringify(barChart, null, 2)}</pre>

            <h2>Pie Chart Data</h2>
            <pre>{JSON.stringify(pieChart, null, 2)}</pre>
        </div>
    );
};

export default App;
